  class Recipe(val element:String, val recipes:Array[Array[String]]) {
    override def toString = element + ":" + recipes.map(_.mkString("+")).mkString(",")
  }
  def toInt(s:String) = {
    s.toInt
  }
  def split(s:String) = {
    if (s.head != '%') throw new Exception("Split on unencoded string: " + s)
    if (s.length == 2) Array.empty[String]
    else s.tail.tail.split(s.substring(0, 2))
  }
  val data = {
    var parts = split(split(sdata)(0))
    var words = split(parts(0))
    split(parts(1)).map(s => {
      var parts = split(s)
      var result = words(parts(0).toInt)
      var recipes = split(parts(1)).map(x=>x.split(",").map(toInt).map(words))
      new Recipe(result, recipes)
    })
  }
  val interim = data.map(recipe => (
    recipe.element,
    recipe.recipes.map(x=>x.sorted.mkString(","))
  ))

  val byElement = interim.toMap;
  val byRecipe = interim.flatMap(r=>r._2.map(x=>(r._1, x)))
    .groupBy(r=>r._2)
    .map(r=>(r._1, r._2.map(x=>x._1)))
    .toMap

  val baseElements = data.filter(x=>x.recipes.length == 0).map(x=>x.element)
}
